mapper.select:
SELECT 

FROM ch_sa_payslip record

GROUP BY  NET_TARGET_YEARMONTH 


		order by ${page.orderBy}

	limit #{page.pageOffset} , #{page.pageSize}

where.if:

	 and record.NAME  = #{entity.name} 


	 AND NET_TARGET_YEARMONTH BETWEEN #{entity.startMonth} AND #{entity.endMonth} 

and record.status = 0

mapper.select:
select card_no from ch_sa_payslip where card_no in
#{item}
and NET_TARGET_YEARMONTH = #{targetMonth} and status=0

SELECT DISTINCT ${column} FROM ch_sa_payslip where status=0
and left(NET_TARGET_YEARMONTH,4)= #{year}
and ${column} != '#N/A' and ${column} is not null
